-- If any Oracle Forms PL/SQL routine (triggers, procedures, functions) has only Oracle Forms internal built-ins and/or these custom routines, this routine can be discarded.
DECLARE
   p_prc_name varchar2(1000) := 'M_LOAD_FORM_CUSTOM_DISPOSA: ';

BEGIN
   DBMS_OUTPUT.put_line (p_prc_name);

-- DEFAULT FOR ALL CONVERSIONS

   commit;

   DBMS_OUTPUT.put_line ('COMMIT.');

EXCEPTION
   when others then
      rollback;
      DBMS_OUTPUT.put_line ('Erro ' || p_prc_name || to_char (sqlcode) || ' - ' || sqlerrm);
END;
/

